def chi_square(string):
    frequency_list = frequency_list_generate(string.lower())
    percentage_list = []
    for i in range(len(frequency_list)):
      if sum(frequency_list) == 0:
        percentage_list.append(float(0))
      else:
        percentage_list.append(float(frequency_list[i])/float(sum(frequency_list)))
    diffenece_list = []
    for j in range(len(frequency_list)):
      diffenece_list.append(((percentage_list[j]-EngFreq[j])*(percentage_list[j]-EngFreq[j]))/EngFreq[j])

    chi_score = sum(diffenece_list)
    return(chi_score)
    
      
