really_english_string = ""


def score_collection(string):
    scores = []
    scores.append(IC(string, 1))
    scores.append(chi_square(string))
    scores.append(chi_square_of_bigrams(find_bigrams(string)))
    scores.append(chi_square_of_trigrams(find_trigrams(string)))
    scores.append(bhattacharyya(string))
    scores.append(bhattacharyya_for_bigram(find_bigrams(string)))
    scores.append(bhattacharyya_for_trigram(find_trigrams(string)))
    if frequency_list_generate(string)[4]:
        scores.append(
            frequency_list_generate(string)[4] / sum(frequency_list_generate(string))
        )
    else:
        scores.append("0.0")
    return scores


